import React from "react";
import "./App.css";

const OPERATIONS = { "+": "+" };
class App extends React.Component {
  constructor(props) {
    super(props);
    this.state = {
      calcScreen: "0",
      calcOperation: "",
    };
    this.onClickValue = this.onClickValue.bind(this);
  }

  onClickValue(event) {
    const value = event.target.innerText;

    if (value) {
      if (value === "C") {
        this.setState({ calcScreen: "0", calcOperation: "" });
      } else if (["+", "−", "x", "/"].includes(value)) {
        if (this.state.calcScreen === "0") {
          this.setState({
            calcScreen: "0",
            calcOperation:
              this.state.calcOperation.slice(
                0,
                this.state.calcOperation.length - 1
              ) + value,
          });
        } else {
          this.setState({
            calcScreen: "0",
            calcOperation:
              this.state.calcOperation + this.state.calcScreen + value,
          });
        }
      } else if (
        ["1", "2", "3", "4", "5", "6", "7", "8", "9", "0"].includes(value)
      ) {
        if (this.state.calcScreen === "0") {
          this.setState({
            calcScreen: value,
          });
        } else {
          this.setState({
            calcScreen: this.state.calcScreen + value,
          });
        }
      } else if ("." === value && this.state.calcScreen.search("\\.") < 0) {
        this.setState({
          calcScreen: this.state.calcScreen + value,
        });
      } else if ("=" === value && this.state.calcOperation) {
        console.log(this.state.calcOperation + this.state.calcScreen);

        this.setState({
          calcScreen: eval(this.state.calcOperation + this.state.calcScreen),
        });
      }
    }
    console.log(this.state.calcScreen);
  }

  render() {
    return (
      <div className="container">
        <div className="calc-body">
          <div className="calc-screen">
            <div className="calc-operation">{this.state.calcOperation}</div>
            <div className="calc-typed">
              {this.state.calcScreen}
              {/* <span className="blink-me">_</span> */}
            </div>
          </div>
          <div className="calc-button-row">
            <div className="button c" onClick={this.onClickValue}>
              C
            </div>
            <div className="button l">≠</div>
            <div className="button l">%</div>
            <div className="button l" onClick={this.onClickValue}>
              /
            </div>
          </div>
          <div className="calc-button-row">
            <div className="button" onClick={this.onClickValue}>
              7
            </div>
            <div className="button" onClick={this.onClickValue}>
              8
            </div>
            <div className="button" onClick={this.onClickValue}>
              9
            </div>
            <div className="button l" onClick={this.onClickValue}>
              x
            </div>
          </div>
          <div className="calc-button-row">
            <div className="button" onClick={this.onClickValue}>
              4
            </div>
            <div className="button" onClick={this.onClickValue}>
              5
            </div>
            <div className="button" onClick={this.onClickValue}>
              6
            </div>
            <div className="button l" onClick={this.onClickValue}>
              −
            </div>
          </div>
          <div className="calc-button-row">
            <div className="button" onClick={this.onClickValue}>
              1
            </div>
            <div className="button" onClick={this.onClickValue}>
              2
            </div>
            <div className="button" onClick={this.onClickValue}>
              3
            </div>
            <div className="button l" onClick={this.onClickValue}>
              +
            </div>
          </div>
          <div className="calc-button-row">
            <div className="button" onClick={this.onClickValue}>
              .
            </div>
            <div className="button" onClick={this.onClickValue}>
              0
            </div>
            <div className="button">
              <span>&#177;</span>
            </div>
            <div className="button l" onClick={this.onClickValue}>
              =
            </div>
          </div>
        </div>
      </div>
    );
  }
}

export default App;
