import React from "react";
import "./App.css";

class App extends React.Component {
  constructor(props) {
    super(props);
    this.state = {
      calcScreen: "",
    };
    this.onClickValue = this.onClickValue.bind(this);
  }

  onClickValue(event) {
    const value = event.target.innerText;
    if (value) {
      if (value === "C") {
        this.setState({ calcScreen: "" });
      } else {
        this.setState({
          calcScreen: this.state.calcScreen + value,
        });
      }
    }

    console.log(this.state.calcScreen);
  }
  render() {
    return (
      <div className="container">
        <div className="calc-body">
          <div className="calc-screen">
            <div className="calc-operation">2536 + 419 + </div>
            <div className="calc-typed">
              {this.state.calcScreen}
              <span className="blink-me">_</span>
            </div>
          </div>
          <div className="calc-button-row">
            <div className="button c" onClick={this.onClickValue}>
              C
            </div>
            <div className="button l">≠</div>
            <div className="button l">%</div>
            <div className="button l">/</div>
          </div>
          <div className="calc-button-row">
            <div className="button" onClick={this.onClickValue}>
              7
            </div>
            <div className="button" onClick={this.onClickValue}>
              8
            </div>
            <div className="button" onClick={this.onClickValue}>
              9
            </div>
            <div className="button l">x</div>
          </div>
          <div className="calc-button-row">
            <div className="button" onClick={this.onClickValue}>
              4
            </div>
            <div className="button" onClick={this.onClickValue}>
              5
            </div>
            <div className="button" onClick={this.onClickValue}>
              6
            </div>
            <div className="button l">−</div>
          </div>
          <div className="calc-button-row">
            <div className="button" onClick={this.onClickValue}>
              1
            </div>
            <div className="button" onClick={this.onClickValue}>
              2
            </div>
            <div className="button" onClick={this.onClickValue}>
              3
            </div>
            <div className="button l">+</div>
          </div>
          <div className="calc-button-row">
            <div className="button">.</div>
            <div className="button" onClick={this.onClickValue}>
              0
            </div>
            <div className="button">
              <span>&#177;</span>
            </div>
            <div className="button l">=</div>
          </div>
        </div>
      </div>
    );
  }
}

export default App;
